import React, { useState, useRef, useEffect } from "react";
import CanvasComponent from "./canvas";
import DisplayWordsComponent from "./drawPrompt";

const Draw = () => {
  const intervalRef = useRef(null);
  const [remainingTime, setRemainingTime] = useState(15);

  const startTimer = () => {
    intervalRef.current = setInterval(() => {
      setRemainingTime((prevTime) => {
        return prevTime === 1 ? 15 : prevTime - 1;
      });
    }, 1000);
  };

  // Use effect hook to log the updated remainingTime
  useEffect(() => {
    console.log(remainingTime);

    // Clean up the interval on component unmount
    return () => {
      clearInterval(intervalRef.current);
    };
  }, [remainingTime]); // This ensures the effect is triggered whenever remainingTime changes

  return (
    <div className="Game">
      <DisplayWordsComponent remainingTime={remainingTime} />
      <CanvasComponent startTimer={startTimer} />
    </div>
  );
};

export default Draw;
