import React, { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
//import { chosenWords } from '../game/features/board/boardSlice';

const DisplayWordsComponent = () => {
  const board = useSelector((state) => state.board); // Assuming the board is stored in the 'board' slice of the state
  const dispatch = useDispatch();
/*
  useEffect(() => {
    // Dispatch the action to choose and display the words
    dispatch(chosenWords());

    // Clear the words after 15 seconds
    const timer = setTimeout(() => {
      dispatch(chosenWords([])); // Pass an empty array to clear the words
    }, 15000);

    return () => {
      clearTimeout(timer); // Clean up the timer on component unmount
    };
  }, [dispatch]);*/
  return (
    <div>
      <h2>Displaying Words:</h2>
      <ul>
        {board.map((card) => (
          <li key={card.id}>{card.contents}</li>
        ))}
      </ul>
    </div>
  );
};

export default DisplayWordsComponent;
