import React, { useState, useEffect } from 'react';
import { useSelector } from 'react-redux';

const DisplayWordsComponent = () => {
  const board = useSelector((state) => state.board); // Assuming the board is stored in the 'board' slice of the state
  const [currentWordIndex, setCurrentWordIndex] = useState(0);
  const [currentTeam, setCurrentTeam] = useState(1);

  const currentTeamWords =
    currentTeam === 1
      ? board.filter((_, index) => index % 2 === 0)
      : board.filter((_, index) => index % 2 === 1);

  useEffect(() => {
    const timer = setInterval(() => {
      // Increment the word index
      setCurrentWordIndex((prevIndex) => prevIndex + 1);

      // If all words for the current team have been displayed, switch to the next team
      if (currentWordIndex === currentTeamWords.length - 1) {
        setCurrentTeam((prevTeam) => (prevTeam === 1 ? 2 : 1)); // Switch to the next team
        setCurrentWordIndex(0); // Reset the word index for the new team
      }
    }, 5000);

    return () => {
      clearInterval(timer); // Clean up the timer on component unmount
    };
  }, [currentTeam, currentWordIndex, currentTeamWords.length]);

  return (
    <div>
      <h2>Displaying Words:</h2>
      {currentWordIndex < currentTeamWords.length ? ( // Check if there are more words to display
        <>
          <h3>Team {currentTeam} Words:</h3>
          <ul>
            {currentTeamWords.map((card, index) => (
              <li key={card.id} style={{ display: index === currentWordIndex ? 'list-item' : 'none' }}>
                {card.contents}
              </li>
            ))}
          </ul>
        </>
      ) : (
        <p>Game Finished!</p>
      )}
    </div>
  );
};

export default DisplayWordsComponent;
