import React, { useState, useEffect } from 'react';
import { useSelector } from 'react-redux';

const DisplayWordsComponent = () => {
  const board = useSelector((state) => state.board); // Assuming the board is stored in the 'board' slice of the state
  const [currentWordIndex, setCurrentWordIndex] = useState(0);
  const teamOneWords = board.filter((_, index) => index % 4 === 0 || index % 4 === 1);
  const teamTwoWords = board.filter((_, index) => index % 4 === 2 || index % 4 === 3);

  useEffect(() => {
    // Move to the next word every 10 seconds
    const timer = setInterval(() => {
      setCurrentWordIndex((prevIndex) =>
        prevIndex === Math.max(teamOneWords.length, teamTwoWords.length) - 1
          ? 0
          : prevIndex + 1
      );
    }, 3000);

    return () => {
      clearInterval(timer); // Clean up the timer on component unmount
    };
  }, [teamOneWords.length, teamTwoWords.length]);

  return (
    <div>
      <h2>Team 1 Words:</h2>
      <ul>
        {teamOneWords.map((card, index) => (
          <li key={card.id} style={{ display: index === currentWordIndex ? 'list-item' : 'none' }}>
            {card.contents}
          </li>
        ))}
      </ul>
      <h2>Team 2 Words:</h2>
      <ul>
        {teamTwoWords.map((card, index) => (
          <li key={card.id} style={{ display: index === currentWordIndex ? 'list-item' : 'none' }}>
            {card.contents}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default DisplayWordsComponent;
