import React, { useState, useEffect } from 'react';
import { useSelector } from 'react-redux';

const DisplayWordsComponent = () => {
  const board = useSelector((state) => state.board); // Assuming the board is stored in the 'board' slice of the state
  const [currentWordIndex, setCurrentWordIndex] = useState(0);
  const [currentTeam, setCurrentTeam] = useState(1);
  const [gameFinished, setGameFinished] = useState(false);

  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentWordIndex((prevIndex) => (prevIndex + 1) % board.length);
      setCurrentTeam((prevTeam) => (prevTeam === 1 ? 2 : 1));
    }, 5000);

    return () => {
      clearInterval(timer); // Clean up the timer on component unmount
    };
  }, [board.length]);

  const currentTeamWords =
    currentTeam === 1
      ? board.filter((_, index) => index % 2 === 0)
      : board.filter((_, index) => index % 2 === 1);

  useEffect(() => {
    // Check if both team word arrays are empty
    if (currentTeamWords.length === 0) {
      // If both teams have no words left to display, and there are words in the board
      setGameFinished(true);
    }
  }, [currentTeamWords.length, board.length]);

  if (gameFinished) {
    return <p>Finished!</p>;
  }

  return (
    <div>
      <h2>Displaying Words:</h2>
      <h3>Team {currentTeam} Words:</h3>
      <ul>
        {currentTeamWords.map((card, index) => (
          <li key={card.id} style={{ display: index === currentWordIndex ? 'list-item' : 'none' }}>
            {card.contents}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default DisplayWordsComponent;
