const initialState = {
  currentTurn: 0,
};
//turn reducer
export const turnReducer = (state = initialState, action) => {
  switch (action.type) {
    case "END_TURN":
      return {
        ...state,
        currentTurn: state.currentTurn === 1 ? 2 : 1,
      };

    default:
      return state;
  }

  //action creator
};
export const endTurn = () => ({
  type: 'END_TURN'
});

export const selectCurrentTurn = (state) => state.turn.currentTurn
