import React, { useEffect } from 'react';
import { CardRow } from './cardRow/CardRow.js';
import { useSelector, useDispatch } from 'react-redux';
import { selectBoard, selectMatchedIDs } from './boardSlice.js';
import { matchOver, selectCurrentTurn } from '../turns/turn.js';

export const Board = () => {
  const currentBoard = useSelector(selectBoard);
  const matchedIDs = useSelector(selectMatchedIDs);
  const turn = useSelector(selectCurrentTurn)
  const numberOfCards = currentBoard.length;
  const columns = 3;
  const rows = Math.floor(numberOfCards / columns);
  const dispatch = useDispatch();
  const team1score = useSelector((state) => state.team.team1Players.score);
  const team2score = useSelector((state) => state.team.team2Players.score);
  const team1name = useSelector((state) => state.team.team1Players[0].name);
  const team2name = useSelector((state) => state.team.team2Players[0].name);
  useEffect(() => {

  const matchIsOverHandler = () => {
    dispatch(matchOver(team1name,team1score,team2name,team2score));
  };
      if (matchedIDs.length === 12 && turn>0) {
        matchIsOverHandler();
        console.log(team1name,team1score,team2name,team2score)
      }
  }, [matchedIDs,dispatch,turn,team1name,team1score,team2name,team2score]);

  const getRowCards = (row) => {
    const rowCards = [];
    for (let j = 0; j < columns; j++) {
      const cardIndex = row * columns + j;
      rowCards.push(currentBoard[cardIndex]);
    }
    return rowCards;
  };

  let content = [];
  for (let row = 0; row < rows; row++) {
    const rowCards = getRowCards(row);
    content.push(
      <CardRow
        key={row}
        cards={rowCards}
      />
    );
  }

  return <div className="cards-container">{content}</div>;
};
