import React from 'react';
import { useSelector, useDispatch } from 'react-redux';
import {
  selectCurrentTurn,
  endTurn,
} from '../../../turns/turn';
import {
  selectVisibleIDs,
  flipCard,
  selectMatchedIDs,
  resetCards,
  selectBoard, // Import the selector to access board state
} from '../../boardSlice';

export const Card = ({ id }) => {
  const visibleIDs = useSelector(selectVisibleIDs);
  const matchedIDs = useSelector(selectMatchedIDs);
  const teamID = useSelector(selectCurrentTurn);
  const board = useSelector(selectBoard); // Get the entire board state
  const dispatch = useDispatch();

  const flipHandler = (id) => {
    dispatch(flipCard(id, teamID));
  };

  const ResetCardHandler = () => {
    setTimeout(() => {
      dispatch(resetCards());
    }, 2000);
  };

  const EndTurn = () => {
    dispatch(endTurn());
  };

  let cardStyle = 'resting';
  let click = () => flipHandler(id);
  let cardContent = null; // Initialize with null

  const card = board.find((card) => card.id === id); // Find the card in the board by ID

  if (card) {
    if (visibleIDs.includes(id) || matchedIDs.includes(id)) {
      if (card.image) {
        cardContent = <img src={card.image} alt="Card option" />;
      } else {
        cardContent = card.contents;
      }
      click = () => {};
    }

    if (matchedIDs.includes(id)) {
      cardStyle = 'matched';
    }

    if (visibleIDs.length === 2) {
      click = () => {};
    }

    if (visibleIDs.length >= 2 && !matchedIDs.includes(id)) {
      cardStyle = 'no-match';
      ResetCardHandler();
      EndTurn();
    }
  }

  return (
    <button onClick={click} className={`card ${cardStyle}`}>
      {cardContent}
    </button>
  );
};
